    }

    render(){

     let items = [];
     this.items
    .forEach(item => items.push(
        <tr key={item.id}>
        <td>{item.id}</td>
        <td>{item.nombre_producto}</td>
        <td>{item.referencia}</td>
        <td>{item.precio}</td>
        <td>{item.peso}</td>
        <td>{item.categoria}</td>
        <td>{item.stock}</td>
        <td>{item.fecha_creacion}</td>
        <td>{item.fecha_venta}</td>
        <td><Button variant="success" size="sm">Editar</Button> <Button variant="danger" size="sm">Eliminar</Button></td>
        </tr> 
        ))                  

        return (
            <div className="container">
               <Table striped bordered hover>
                    <thead>
                        <tr>
                        <th>#</th>
                        <th>Nombre de producto</th>
                        <th>Referencia</th>
                        <th>Precio</th>
                        <th>Peso</th>
                        <th>Categoria</th>
                        <th>Stock</th>                        
                        <th>Fecha de creaci√≥n</th>
                        <th>Fecha de Venta</th>
                        <th>Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {items}                      
                    </tbody>
                </Table>        
            </div>
          )
     }



}

