import React from 'react'
import PropTypes from 'prop-types'
import axios from 'axios'


class ContainerProduct extends React.Component{

    constructor (props){
        super(props)

        this.state = {
            items: []
        }
    }    

    componentDidMount (){
       axios.get('http://localhost/api/product.php')
       .then(function (response) {
        console.log(response.data);   
        this.items = response.data



       })
       .catch(function (error) {
           //handle error
           console.log(error);
       });

    }

    componentDidUpdate(){


    }

    renderTableData() {
        return this.state.items.map((item, index) => {
           const { id, nombre_producto, referencia, precio, peso, categoria, stock, fecha_creacion, fecha_venta } = item //destructuring
           return (
              <tr key={id}>
                 <td>{id}</td>
                 <td>{nombre_producto}</td>
                 <td>{referencia}</td>
                 <td>{precio}</td>
                 <td>{peso}</td>
                 <td>{categoria}</td>                 
                 <td>{stock}</td>                
                 <td>{fecha_creacion}</td>
                 <td>{fecha_venta}</td>
              </tr>
           )
        })
     }

    render(){

        return (
            <table>
                <thead>
                    <th>id</th>
                    <th>Nombre del producto</th>
                    <th>referencia</th>
                    <th>precio</th>
                    <th>peso</th>
                    <th>categoria</th>
                    <th>stock</th>
                    <th>fecha de creación</th>
                    <th>fecha de venta</th>
                </thead>
            <tbody>
              {this.state.items.map(function(item) {

                return (
                  <tr>
                    <td>{item.id}</td>
                    <td>{item.nombre_producto}</td>
                    <td>{item.referencia}</td>
                    <td>{item.precio}</td>
                    <td>{item.peso}</td>
                    <td>{item.categoria}</td>
                    <td>{item.stock}</td>
                    <td>{item.fecha_creacion}</td>
                    <td>{item.fecha_venta}</td>
                  </tr>
                );
              })}
            </tbody>
            </table>
          )
     }



}

ContainerProduct.propTypes = {
    name: PropTypes.string.isRequired

}

export default ContainerProduct
