import React from 'react'
import PropTypes from 'prop-types'
import axios from 'axios'


class ContainerProduct extends React.Component{

    constructor (props){
        super(props)

        this.state = {
            items: []
        }
    }    

    componentDidMount (){
       axios.get('http://localhost/api/product.php')
       .then(function (response) {
        console.log(response.data);   
        this.items = response.data



       })
       .catch(function (error) {
           //handle error
           console.log(error);
       });

    }

    componentDidUpdate(){


    }

    renderTableData() {
        return this.state.items.map((item, index) => {
           const { id, nombre_producto, referencia, precio, peso, categoria, stock, fecha_creacion, fecha_venta } = item //destructuring
           return (
              <tr key={id}>
                 <td>{id}</td>
                 <td>{nombre_producto}</td>
                 <td>{referencia}</td>
                 <td>{precio}</td>
                 <td>{peso}</td>
                 <td>{categoria}</td>
                 <td>{referencia}</td>
                 <td>{stock}</td>                
                 <td>{fecha_creacion}</td>
                 <td>{fecha_venta}</td>
              </tr>
           )
        })
     }

    render(){

        return (
            <section className="product">
               <table id='items'>
               <tbody>
                  {this.renderTableData()}
               </tbody>
            </table>
            </section>

        )
     }



}

ContainerProduct.propTypes = {
    name: PropTypes.string.isRequired

}

export default ContainerProduct
