import React from 'react'
import PropTypes from 'prop-types'
import axios from 'axios'
import Table from 'react-bootstrap/Table'
import Button from 'react-bootstrap/Button'

class ContainerProduct extends React.Component{
    constructor(props){
        super()
    this.state = {
        items: []
    }
    }
    componentWillMount(){
        axios.get('http://localhost/api/view/product.php')
        .then((response)=>{
         this.setState({
          items: response.data   
         })

         console.log(this.state.items)

        })
        .catch(function (error) {
            //handle error
            console.log(error);
        });

    }

    componentDidMount (){


    }

    componentDidUpdate(){


    }
    renderTable(){
        const items = [];
  this.state.items
    .forEach(item => items.push(
                       <li>
                           id={item.id} key={item.id} text={item.text}
                       </li>
                     ))

  return(
     <ul>{items}</ul>
  );
    }

    render(){
        let items = this.state.items.map((item) => {
            return (
                <tr key={item.id}>
                <td>{item.id}</td>
                <td>{item.nombre_producto}</td>
                <td>{item.referencia}</td>
                <td>{item.precio}</td>
                <td>{item.peso}</td>
                <td>{item.categoria}</td>
                <td>{item.stock}</td>
                <td>{item.fecha_creacion}</td>
                <td>{item.fecha_venta}</td>
                <td><Button variant="success" size="sm">Editar</Button> <Button variant="danger" size="sm">Eliminar</Button></td>
                </tr> 
            )

         });
        return (
            <div className="container">
               <Table striped bordered hover>
                    <thead>
                        <tr>
                        <th>#</th>
                        <th>Nombre de producto</th>
                        <th>Referencia</th>
                        <th>Precio</th>
                        <th>Peso</th>
                        <th>Categoria</th>
                        <th>Stock</th>                        
                        <th>Fecha de creación</th>
                        <th>Fecha de Venta</th>
                        <th>Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                        <td>1</td>
                        <td>Mark</td>
                        <td>Otto</td>
                        <td>@mdo</td>
        <td></td>
                        <td>Mark</td>
                        <td>Otto</td>
                        <td>@mdo</td>
                        <td>@mdo</td>
                        <td><Button variant="success" size="sm">Editar</Button> <Button variant="danger" size="sm">Eliminar</Button></td>
                        </tr>                       
                    </tbody>
                </Table>        
            </div>
          )
     }



}

