import React from 'react'
import PropTypes from 'prop-types';
import axios from 'axios';
import Product from './Product'

class ContainerProduct extends React.Component{

    constructor (props){
        super(props)

        this.state = {
            items: []
        }
    }    

    componentDidMount (){
        var config = {
            headers: {'Access-Control-Allow-Origin': '*'}
        };
        axios.get('http://localhost/api/product.php', config)
              .then(function (response) {
                    console.log(response);
                             })
             .catch(function (error) {
                    //handle error                                  
                    console.log(error);
                });

        var miInit = { method: 'GET',               
               mode: 'no-cors',
               cache: 'default' };
       fetch('http://localhost/api/product.php', miInit)
       .then(response => console.log(response))
       .then(itemsJson => this.setState({items: itemsJson}))


    }

    componentDidUpdate(){


    }

    render(){
        const name = this.state.name
        return <Product name={name} />
    }



}

ContainerProduct.propTypes = {
    name: PropTypes.string.isRequired

}

export default ContainerProduct
