import React from 'react'
import PropTypes from 'prop-types'
import axios from 'axios'
import {Table,Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';


class ContainerProduct extends React.Component {
    constructor(props) {
        super()
        this.state = {
            items: [],
            newProductModal: false,
        }
    }
    componentWillMount() {
        axios.get('http://localhost/api/view/product.php')
            .then((response) => {
                this.setState({
                    items: response.data
                })

                console.log(this.state.items)

            })
            .catch(function (error) {
                //handle error
                console.log(error);
            });

    }

    toggleNewProductModal(){
        this.setState({
            newProductModal:true
        });
        //this.state.newProductModal = true;

    }    

    render() {
        let items = this.state.items.map((item) => {
            return (
                <tr key={item.id}>
                    <td>{item.id}</td>
                    <td>{item.nombre_producto}</td>
                    <td>{item.referencia}</td>
                    <td>{item.precio}</td>
                    <td>{item.peso}</td>
                    <td>{item.categoria}</td>
                    <td>{item.stock}</td>
                    <td>{item.fecha_creacion}</td>
                    <td>{item.fecha_venta}</td>
                    <td><Button color="success" size="sm">Editar</Button> <Button color="danger" size="sm">Eliminar</Button></td>
                </tr>
            )

        });
        return (
            <div className="container">
                <Button color="primary" onClick={this.toggleNewProductModal.bind(this)}>Agregar producto</Button>
                <Modal isOpen={this.state.newProductModal} toggle={this.toggleNewProductModal.bind(this)}>
                    <ModalHeader toggle={this.toggleNewProductModal.bind(this)}>Agregar producto </ModalHeader>
                    <ModalBody>

                    </ModalBody>
                    <ModalFooter>
                        <Button color="primary" onClick={this.toggleNewProductModal.bind(this)}>Do Something</Button>{' '}
                        <Button color="secondary" onClick={this.toggleNewProductModal.bind(this)}>Cancel</Button>
                    </ModalFooter>
                </Modal>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Nombre de producto</th>
                            <th>Referencia</th>
                            <th>Precio</th>
                            <th>Peso</th>
                            <th>Categoria</th>
                            <th>Stock</th>
                            <th>Fecha de creación</th>
                            <th>Fecha de Venta</th>
                            <th>Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {items}
                    </tbody>
                </Table>
            </div>
        )
    }



}

