import React from 'react'
import PropTypes from 'prop-types'
import axios from 'axios'
import {Table,Button, Modal, ModalHeader, ModalBody, ModalFooter, Label, Input, FormGroup } from 'reactstrap';


class ContainerProduct extends React.Component {
    constructor(props) {
        super()
        this.state = {            
            items: [],
            newProductData:{
               nombre_producto: '',
               referencia: '',
               precio: '',
               peso: '',
               categoria: '',
               stock: '',
               fecha_venta:''
            },
            newProductModal: false,
        }
    }
    componentWillMount() {
        axios.get('http://localhost/api/view/product.php')
            .then((response) => {
                this.setState({
                    items: response.data
                })

                console.log(this.state.items)

            })
            .catch(function (error) {
                //handle error
                console.log(error);
            });

    }

    toggleNewProductModal(){
        this.setState({
            newProductModal:true
        });
        //this.state.newProductModal = true;

    }    

    render() {
        let items = this.state.items.map((item) => {
            return (
                <tr key={item.id}>
                    <td>{item.id}</td>
                    <td>{item.nombre_producto}</td>
                    <td>{item.referencia}</td>
                    <td>{item.precio}</td>
                    <td>{item.peso}</td>
                    <td>{item.categoria}</td>
                    <td>{item.stock}</td>
                    <td>{item.fecha_creacion}</td>
                    <td>{item.fecha_venta}</td>
                    <td><Button color="success" size="sm">Editar</Button> <Button color="danger" size="sm">Eliminar</Button></td>
                </tr>
            )

        });
        return (
            <div className="container">
                <Button color="primary" onClick={this.toggleNewProductModal.bind(this)}>Agregar producto</Button>
                <Modal isOpen={this.state.newProductModal} toggle={this.toggleNewProductModal.bind(this)}>
                    <ModalHeader toggle={this.toggleNewProductModal.bind(this)}>Agregar producto </ModalHeader>
                    <ModalBody>
                    <FormGroup>
                    <Label for="nombre del producto">Nombre del producto</Label>
                    <Input type="text" name="nombre_producto" id="nombre_producto"  placeholder="ingresar normbre del producto" value={this.state.newProductData.nombre_producto}
                    onChange={(e) => {
                        let {newProductData} = this.state;
                        newProductData.nombre_producto = e.target.value;
                        this.setState({newProductData})
                    }} />
                    <Label for="referencia">Referencia</Label>
                    <Input type="number" name="referencia" id="referencia" placeholder="ingresar referencia" />
                    <Label for="precio">Precio</Label>
                    <Input type="number" name="precio" id="precio" placeholder="ingresar precio" />
                    <Label for="peso">Peso</Label>
                    <Input type="number" name="peso" id="peso" placeholder="ingresar peso" />
                    <Label for="categoria">Categoría</Label>
                    <Input type="text" name="categoria" id="categoria" placeholder="ingresar categoria" />
                    <Label for="stock">Stock</Label>
                    <Input type="text" name="stock" id="stock" placeholder="ingresar stock" />                    
                    <Label for="fecha_venta">Fecha venta</Label>
                    <Input
                    type="datetime"
                    name="fecha_venta"
                    id="fecha_venta"
                    placeholder="ingresar fecha venta"
                    />
                    </FormGroup>
                    </ModalBody>
                    <ModalFooter>
                        <Button color="primary" onClick={this.toggleNewProductModal.bind(this)}>Do Something</Button>{' '}
                        <Button color="secondary" onClick={this.toggleNewProductModal.bind(this)}>Cancel</Button>
                    </ModalFooter>
                </Modal>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Nombre de producto</th>
                            <th>Referencia</th>
                            <th>Precio</th>
                            <th>Peso</th>
                            <th>Categoria</th>
                            <th>Stock</th>
                            <th>Fecha de creación</th>
                            <th>Fecha de Venta</th>
                            <th>Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {items}
                    </tbody>
                </Table>
            </div>
        )
    }



}

